package org.iets3.analysis.base.plugin;

/*Generated by MPS */

import jetbrains.mps.project.MPSProject;
import com.intellij.openapi.wm.StatusBar;
import com.intellij.openapi.wm.WindowManager;

public class ISolvableMonitorNotification {
  private final MPSProject project;
  private StatusBar myStatusBar;
  private ISolvableMonitorWidget widget;


  public ISolvableMonitorNotification(MPSProject prj) {
    this.project = prj;
  }

  public void projectOpen() {
    myStatusBar = WindowManager.getInstance().getStatusBar(project.getProject());
    if (myStatusBar != null) {
      widget = new ISolvableMonitorWidget(myStatusBar);
      myStatusBar.addWidget(widget);
      widget.update();
    }
  }

  public void projectClosed() {
    if (myStatusBar != null) {
      myStatusBar.removeWidget(widget.ID());
      this.myStatusBar = null;
      widget = null;
    }
  }

}
