package org.iets3.core.expr.simpleTypes.plugin;

/*Generated by MPS */

import java.math.BigDecimal;
import org.apache.commons.lang3.StringUtils;

public class DecimalHelper {

  public static BigDecimal makeWithPrecision(double d, int reqPrec) {
    String s = d + "";
    if (!(s.contains("."))) {
      s = s + "." + StringUtils.repeat("0", reqPrec);
    } else {
      int p = s.indexOf(".");
      int delta = s.length() - p;
      s = s + StringUtils.repeat("0", delta - 1);
    }
    return new BigDecimal(s);
  }

}
