import com.intellij.ui.JBColor;
import java.awt.Color;
import org.iets3.core.expr.tests.behavior.IEvalResult;

public class TestColors {

  public static final JBColor RED = new JBColor(new Color(252, 225, 231), new Color(113, 11, 34));
  public static final JBColor GREEN = new JBColor(new Color(225, 252, 227), new Color(6, 59, 10));
  public static final JBColor YELLOW = new JBColor(new Color(247, 250, 177), new Color(102, 106, 7));

  public static final String INCOMPLETE = "[incomplete]";
  public static final String SUCCESS = "[success]";
  public static final String FAIL = "[fail]";

  public static Color colorForItem(IEvalResult res) {
    if (res == null) {
      return null;
    }
    if (res.isOk()) {
      return GREEN;
    }
    if (!(res.isOk())) {
      return RED;
    }
    return null;
  }

  public static Color colorForTestStatus(String status) {
    if (INCOMPLETE.equals(status)) {
      return YELLOW;
    }
    if (SUCCESS.equals(status)) {
      return GREEN;
    }
    if (FAIL.equals(status)) {
      return RED;
    }
    return null;
  }


}
