public class Stopwatch {

  private long tLast;
  private long tAccum;

  public static Stopwatch start() {
    return new Stopwatch();
  }

  private Stopwatch() {
    this.tLast = System.currentTimeMillis();
    this.tAccum = 0L;
  }

  public void restart() {
    this.tLast = System.currentTimeMillis();
  }

  public long peek() {
    return System.currentTimeMillis() - tLast;
  }

  public long time() {
    long t = System.currentTimeMillis();
    long dt = t - tLast;
    tLast = t;
    tAccum += dt;
    return dt;
  }

  public long total() {
    return tAccum;
  }
}
