package org.iets3.variability.artifacts.base.editor;

/*Generated by MPS */

import com.intellij.ui.JBColor;
import java.awt.Color;
import jetbrains.mps.nodeEditor.MPSColors;
import org.iets3.variability.artifacts.base.behavior.PresenceConditionValue;
import jetbrains.mps.ide.MPSCoreComponents;
import jetbrains.mps.openapi.editor.style.StyleRegistry;

public class ColorHelper {
  public static JBColor UNDEFINED_PRESENCE_CONDITION = new JBColor(new Color(252, 220, 180), MPSColors.ORANGE);
  public static JBColor FALSE_PRESENCE_CONDITION = new JBColor(new Color(255, 188, 188), MPSColors.RED);
  public static JBColor TRUE_PRESENCE_CONDITION = new JBColor(new Color(204, 255, 204), MPSColors.DARK_GREEN);

  public static JBColor foregroundColorByState(PresenceConditionValue iVariationPointState) {
    if (iVariationPointState == null) {
      // normal edit mode
      return ArtifactColors.PRESENCE_CONDITION;
    } else {
      // config preview mode
      return new JBColor(new Color(0, 60, 0), new Color(20, 60, 20));
    }
  }

  public static JBColor backgroundColorByState(PresenceConditionValue iVariationPointState) {
    if (iVariationPointState == null) {
      // normal edit mode
      Color editorBackground = MPSCoreComponents.getInstance().getPlatform().findComponent(StyleRegistry.class).getEditorBackground();
      return new JBColor(editorBackground, editorBackground);
    } else {
      // config preview mode
      switch (iVariationPointState) {
        case UNDEFINED:
          return UNDEFINED_PRESENCE_CONDITION;
        case FALSE:
          return FALSE_PRESENCE_CONDITION;
        case TRUE:
          return TRUE_PRESENCE_CONDITION;
        default:
          // should not occur
          return JBColor.GRAY;
      }
    }
  }
}
