import javax.swing.JButton;
import javax.swing.Icon;
import javax.swing.JToggleButton;
import javax.swing.AbstractButton;
import java.awt.Dimension;
import java.awt.Insets;

public class ButtonBuilder {

  public static JButton createButton(Icon icon, String tooltip) {
    JButton button = new JButton();
    init(button, icon, tooltip);
    return button;
  }

  public static JToggleButton createToggleButton(Icon icon, String tooltip) {
    JToggleButton button = new JToggleButton();
    init(button, icon, tooltip);
    return button;
  }

  private static void init(AbstractButton button, final Icon icon, String tooltip) {
    button.setBorderPainted(false);
    button.setContentAreaFilled(false);
    button.setOpaque(false);
    button.setFocusPainted(false);
    button.setPreferredSize(new Dimension(icon.getIconWidth() + 14, icon.getIconHeight() + 16));
    button.setMargin(new Insets(0, 0, 0, 0));
    button.setIcon(icon);
    button.setToolTipText(tooltip);
  }
}
